  def self.get_service id
    soap_body = '<soap:Body><ldb:GetServiceDetailsByRIDRequest><ldb:rid>' + id + '</ldb:rid></ldb:GetServiceDetailsByRIDRequest></soap:Body></soap:Envelope>'
    begin
      if TEST
        xml = Nokogiri::XML(TEST_DATA[:service])
      else
        response = RestClient.post URL, SOAP_HEADERS + soap_body, content_type: 'text/xml'
        if DEBUG
          print "*****************\n"
          print response.body
          print "\n*****************\n"
        end
        xml = Nokogiri::XML(response.body)
      end
    rescue => e
      puts e.inspect
      return
    end
    xml.remove_namespaces!
    result = xml.xpath('/Envelope/Body/GetServiceDetailsByRIDResponse/GetServiceDetailsResult')

    service = Service.new
    service.rid = result.xpath('rid').text
    service.id = service.rid
    service.uid = result.xpath('uid').text
    service.train_id = result.xpath('trainid').text
    service.rsid = result.xpath('rsid').text
    service.sdd = result.xpath('sdd').text
    service.operator = Operator.find_by code: result.xpath('operatorCode').text
    service.service_type = result.xpath('serviceType').text
    service.category = ReferenceDataHelper::CATEGORY_CODES[result.xpath('category').text]
    reason = Reason.find_by code: result.xpath('cancelReason').text
    service.cancelled_reason = reason.cancellation_reason if reason
    reason = Reason.find_by code: result.xpath('delayReason').text
    service.delayed_reason = reason.late_running_reason if reason

    if result.xpath('locations').children.size > 0
      result.xpath('locations').xpath('location').each do |calling_point_xml|
        service.calling_points << create_calling_point(calling_point_xml)
      end
    end

    service.origin = service.calling_points.first.station || service.calling_points.first.tiploc
    service.destination = service.calling_points.last.station || service.calling_points.last.tiploc

    return service
  end

  # Call API to update reason codes
  def self.update_reson_codes
    soap_body = '<soap:Body><ldb:GetReasonCodeListRequest/></soap:Body></soap:Envelope>'
    begin
      if TEST
        xml = Nokogiri::XML(TEST_DATA[:service])
      else
        response = RestClient.post URL, SOAP_HEADERS + soap_body, content_type: 'text/xml'
        if DEBUG
          print "*****************\n"
          print response.body
          print "\n*****************\n"
        end
        xml = Nokogiri::XML(response.body)
      end
    rescue => e
      puts e.inspect
      return
    end
    xml.remove_namespaces!
    result = xml.xpath('/Envelope/Body/GetReasonCodeListResponse/GetReasonCodeListResult/reason')
    result.each do |reason_xml|
      reason = {
        code: reason_xml.xpath('code').text.strip,
        late_running_reason: reason_xml.xpath('lateReason').text.strip,
        cancellation_reason: reason_xml.xpath('cancReason').text.strip
      }
      print reason
      update = Reason.where(code: reason[:code]).first_or_create
      print update
      update.update_attributes(late_running_reason: reason[:late_running_reason], cancellation_reason: reason[:cancellation_reason]) if update
    end

    return
  end


