  def self.get_board type, crs
    time = (Time.now - 10.minutes).strftime(DATE_TIME_FORMAT)
    limit = '10'
    soap_body = '<soap:Body><ldb:Get' + type + 'WithDetailsRequest><ldb:crs>' + crs + '</ldb:crs><ldb:numRows>' + limit + '</ldb:numRows><ldb:time>' + time + '</ldb:time><ldb:timeWindow>200</ldb:timeWindow><ldb:filtercrs></ldb:filtercrs><ldb:filterType>to</ldb:filterType><ldb:filterTOC></ldb:filterTOC><ldb:services>PBS</ldb:services><ldb:getNonPassengerServices>false</ldb:getNonPassengerServices></ldb:Get' + type + 'WithDetailsRequest></soap:Body></soap:Envelope>'
    begin
      if TEST
        xml = Nokogiri::XML(TEST_DATA[type])
      else
        response = RestClient.post URL, SOAP_HEADERS + soap_body, content_type: 'text/xml'
        if DEBUG
          print "*****************\n"
          print response.body
          print "\n*****************\n"
        end
        xml = Nokogiri::XML(response.body)
      end
    rescue => e
      puts e.inspect
      return
    end
    xml.remove_namespaces!
    result = xml.xpath('/Envelope/Body/Get' + type + 'WithDetailsResponse/GetBoardWithDetailsResult')

    station_board = StationBoard.new
    station = Station.find_by crs: result.xpath('crs').text
    result.xpath('nrccMessages').children.each do |message|
      station_board.nrcc_messages << NrccMessage.new(
        message.xpath('category').text,
        message.xpath('severity').text,
        ActionView::Base.full_sanitizer.sanitize(message.xpath('xhtmlMessage').text))
    end

    result.xpath('trainServices').children.each do |train_service|
      service = Service.new
      service.station = station

      crs = train_service.xpath('origin/location[1]/crs').text
      tiploc = train_service.xpath('origin/location[1]/tiploc').text
      # Use the tiploc to find the origin and set the station, if it is one
      if tiploc
        service.origin = TimingPoint.find_by code: tiploc
        service.origin = service.origin.station if service.origin and service.origin.station
      elsif crs
        # Otherwise try and use the crs
        service.origin = Station.find_by crs: crs
      end

      crs = train_service.xpath('destination/location[1]/crs').text
      tiploc = train_service.xpath('destination/location[1]/tiploc').text
      # Use the tiploc to find the destination and set the station, if it is one
      if tiploc
        service.destination = TimingPoint.find_by code: tiploc
        service.destination = service.destination.station if service.destination and service.destination.station
      elsif crs
        # Otherwise try and use the crs
        service.destination = Station.find_by crs: crs
      end

      service.sta = get_time train_service.xpath('sta').text
      service.eta = get_time train_service.xpath('eta').text
      service.ata = get_time train_service.xpath('ata').text
      service.std = get_time train_service.xpath('std').text
      service.etd = get_time train_service.xpath('etd').text
      service.atd = get_time train_service.xpath('atd').text
      service.platform = train_service.xpath('platform').text
      service.operator = Operator.find_by code: train_service.xpath('operatorCode').text
      service.category = ReferenceDataHelper::CATEGORY_CODES[train_service.xpath('category').text]
      is_cancelled = train_service.xpath('isCancelled').text
      is_cancelled and is_cancelled == "true" ? service.cancelled = true : service.cancelled = false
      service.rid = train_service.xpath('rid').text
      service.id = service.rid
      service.uid = train_service.xpath('uid').text
      service.train_id = train_service.xpath('trainid').text
      service.sdd = train_service.xpath('sdd').text

      if train_service.xpath('previousLocations').children.size > 0
        train_service.xpath('previousLocations').xpath('location').each do |calling_point_xml|
          service.calling_points << create_calling_point(calling_point_xml)
        end
      end

      calling_point = CallingPoint.new
      calling_point.station = station
      calling_point.tiploc = TimingPoint.find_by station_id: service.station.id
      calling_point.platform = service.platform
      calling_point.sta = service.sta
      calling_point.eta = service.eta
      calling_point.ata = service.ata
      calling_point.std = service.std
      calling_point.etd = service.etd
      calling_point.atd = service.atd
      service.calling_points << calling_point

      if train_service.xpath('subsequentLocations').children.size > 0
        train_service.xpath('subsequentLocations').xpath('location').each do |calling_point_xml|
          service.calling_points << create_calling_point(calling_point_xml)
        end
      end

      station_board.train_services << service
    end

    return station_board
  end

  def self.create_calling_point xml
    calling_point = CallingPoint.new
    calling_point.station = Station.find_by crs: xml.xpath('crs').text
    calling_point.tiploc = TimingPoint.find_by code: xml.xpath('tiploc').text
    calling_point.platform = xml.xpath('platform').text
    length = xml.xpath('length').text
    calling_point.length = length.to_i unless length.nil? or length.empty?
    is_pass = xml.xpath('isPass').text
    is_pass and is_pass == "true" ? calling_point.pass = true : calling_point.pass = false
    departure_type = xml.xpath('departureType').text
    departure_type and departure_type == "NoLog" ? calling_point.no_report = true : calling_point.no_report = false
    activities = xml.xpath('activities').text
    if activities and !activities.empty?
      activities.split(" ").each do |code|
        calling_point.activities << ReferenceDataHelper::ACTIVITY_CODES[code.strip]
      end
    end
    is_cancelled = xml.xpath('isCancelled').text
    is_cancelled and is_cancelled == "true" ? calling_point.cancelled = true : calling_point.cancelled = false
    calling_point.sta = get_time xml.xpath('sta').text
    calling_point.eta = get_time xml.xpath('eta').text
    calling_point.ata = get_time xml.xpath('ata').text
    calling_point.std = get_time xml.xpath('std').text
    calling_point.etd = get_time xml.xpath('etd').text
    calling_point.atd = get_time xml.xpath('atd').text

    return calling_point
  end

  def self.get_time string
    return DateTime.strptime(string, DATE_TIME_FORMAT) unless string.nil? or string.empty?
  end

